import React, { useEffect, useState } from 'react';
import { Routes, Route, NavLink, useNavigate } from "react-router-dom";
// import Slider from "react-slick";
// import $ from "jquery";
import './App.css';

function Main() {

  const [Dday, setDday] = useState(null);

  function setDDay() {
    // Set the date we're counting down to
    let today = new Date();
    let thatDay = new Date();
    thatDay.setDate(today.getDate() + 10);
    let DDay = new Date(thatDay).getTime();
    // Update the count down every 1 second
    let count = setInterval(function () {
      // Get today's date and time
      let now = new Date().getTime();
      // Find the distance between now and the count down date
      let distance = DDay - now;
      // Time calculations for days, hours, minutes and seconds
      let days = Math.floor(distance / (1000 * 60 * 60 * 24));
      let hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
      let seconds = Math.floor((distance % (1000 * 60)) / 1000);
      // Output the result in an element with id="Dday"
      setDday(days + '일 ' + hours + '시간 ' + minutes + '분 ' + seconds + '초');
      // If the count down is over, write some text 
      if (distance < 0) {
        clearInterval(count);
        setDday("편지를 열어보세요.");
      }
    }, 1000);
  }

  const [isMenu, setIsMenu] = useState(false);

  function Menu(props) {
    return (
      <div className={props.props.isMenu ? "menu_outContainer_active" : "menu_outContainer"}>
        <img alt='menu_img' className={props.props.isMenu ? "menu_img_active" : "menu_img"} src='https://cdn-icons-png.flaticon.com/512/2516/2516745.png' onClick={() => {
          props.props.setIsMenu(!props.props.isMenu)
        }}></img>
      </div>
    );
  };

  return (
    <div className='main_background'>
      <Menu props={{ isMenu, setIsMenu }}></Menu>
      <h1><NavLink end to="/login">Home</NavLink></h1>
      <button onClick={setDDay}>Start D-day</button>
      <h5>{Dday}</h5>
    </div>
  )
}

function Redirect(props) {

  const navigater = useNavigate();
  const urlParams = new URL(window.location.href).searchParams;
  const name = urlParams.get('code');
  const urlParamsErro = new URL(window.location.href).searchParams;
  const nameErro = urlParamsErro.get('error_description');

  useEffect(() => {
    if (nameErro === 'User denied access') {
      alert('로그인에 실패하였습니다.');
      navigater('/login');
    }
    const code = { code: name };
    const queryStringBody = Object.keys(code)
      .map(k => encodeURIComponent(k) + "=" + encodeURI(code[k]))
      .join("&");
    fetch("http://13.209.184.10:8080/api/kakao", {
      method: "POST",
      headers: {
        'content-type': 'application/x-www-form-urlencoded;charset=utf-8'
      },
      body: queryStringBody
    })
      .then(res => res.json())
      .then((data) => {
        window.localStorage.setItem('UserID', data);
        console.log('성공:', data);
        navigater('/main');
      })
      .catch((error) => {
        alert('로그인 실패 사유: ', error);
        navigater('/login');
      });
  }, [name, nameErro, navigater]);

  return (
    <div className='redirect_outContainer'>
      <img alt='redirect_img' className='redirect_img' src='https://cdn.dribbble.com/users/563824/screenshots/3633228/media/d876c7712d969c0656302b16b16af2cc.gif'></img>
      <h3 className='redirect_h3'>로그인 중입니다</h3>
    </div>
  )
}

function Login(props) {

  const navigater = useNavigate();
  useEffect(() => {
    if (props.props !== null) {
      navigater('/main')
    }
  }, [navigater, props.props])

  const [isConditions, setIsConditions] = useState(false);
  const [isIndividual, setIsIndividual] = useState(false);

  function loginWithKakao(key, url) {
    window.location.href = `https://kauth.kakao.com/oauth/authorize?response_type=code&client_id=${key}&redirect_uri=${url}`
  };

  function Conditions(props) {
    return (
      <div className={props.props.isConditions ? "login_conditions" : "login_conditions_fade"}>
        <div className='login_conditions_outContainer'>
          <img alt='close' className='login_conditions_img' src='https://cdn-icons-png.flaticon.com/512/463/463612.png' onClick={() => { props.props.setIsConditions(!props.props.isConditions) }}></img>
          <p className='login_conditions_title'>이용약관</p>
          <article className='login_conditions_article'>
            <p>
              1. 동해물과 백두산이 마르고 닳도록 하느님이 보우하사 우리나라 만세 무궁화 삼천리 화려 강산 대한 사람 대한으로 길이 보전하세
            </p>
            <p>
              2. 남산 위에 저 소나무 철갑을 두른 듯 바람 서리 불변함은 우리 기상일세 무궁화 삼천리 화려 강산 대한 사람 대한으로 길이 보전하세
            </p>
            <p>
