import React, { useState } from 'react';
import Slider from "react-slick";
import './ModalStory.css';

function ModalStory(props) {

  const [isChecked, setIsChecked] = useState(false);

  let settings = {
    draggable: false,
    swipe: false,
    arrows: true,
    dots: true,
    infinite: false,
    speed: 1250,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  function toggleCheck() {
    props.props.props.setCheckStory(!props.props.props.checkStory);
  };

  // Setting Cookie
  function setCookie() {
    setIsChecked(!isChecked);
    if (isChecked === false) {
      const expires = new Date();
      expires.setDate(expires.getDate() + 1);
      const expiresDate = expires.toUTCString();
      let setCookie = '';
      setCookie += 'planetter=190301;';
      setCookie += 'Expires=' + expiresDate;
      document.cookie = setCookie;
      console.log('SaveCookie!');
    } else {
      console.log('Opps!');
      delCookie();
    };
  };

  // Delete Cookie
  function delCookie() {
    const expires = new Date();
    expires.setDate(expires.getDate() - 1);
    const expiresDate = expires.toUTCString();
    let setCookie = '';
    setCookie += 'planetter=190301;';
    setCookie += 'Expires=' + expiresDate;
    document.cookie = setCookie;
  };

  return (
    <div className={props.props.props.checkStory ? "modalStory_fade" : "modalStory_nonFade"}>
      <div className="modalStory_outContainer">
        <div className="modalStory_innerContainer">
          <div className="modalStory_close" onClick={toggleCheck}></div>
          <Slider {...settings}>
            <div className='modalStory_outDiv'>
              <div>
                <div className='modalStory_img1'></div>
                <h1 className="modalStory_title">PLANETTER</h1>
              </div>
            </div>
            <div className='modalStory_outDiv'>
              <div>
                <div className='modalStory_img2'></div>
                <h1 className="modalStory_title">PLANETTER</h1>
              </div>
            </div>
            <div className='modalStory_outDiv'>
              <div>
                <div className='modalStory_img3'></div>
                <h1 className="modalStory_title">PLANETTER</h1>
              </div>
            </div>
            <div className='modalStory_outDiv'>
              <div>
                <div className='modalStory_img4'></div>
                <h1 className="modalStory_title">PLANETTER</h1>
              </div>
            </div>
          </Slider>
          <div className='modalStory_cookie'><input type='checkbox' checked={isChecked} onChange={setCookie}></input> Meet again when there's a new story</div>
        </div>
      </div>
    </div>
  );
};

export default ModalStory;
