function ShareButton(props) {

  const basicURL = 'https://angelo-s-library-2.netlify.app/';
  const image_share = 'https://cdn-icons-png.flaticon.com/512/1111/1111905.png';
  const title = 'Planetter - 기다려지는 소식'

  // function of to share throught the twitter
  function shareTwitter() {
    let url = encodeURIComponent(basicURL);
    window.open(`https://twitter.com/intent/tweet?url=${url}&text=${title}`, '소소한 프로젝트', 'width=400, height=400');
  };

  // function of to share throught the facebook
  function shareFacebook() {
    window.open('https://www.facebook.com/sharer/sharer.php?u='
      + encodeURIComponent(basicURL)
      + '&t=' + encodeURIComponent(title),
      'facebooksharedialog',
      'menubar=no, toolbar=no, resizable=yes, scrollbars=yes, height=400, width=400');
  };

  const [checkMainButton, setCheckMainButton] = useState(false);
  const [checkShare, setCheckShare] = useState(false);

  useEffect(() => {
    if (props.props.openDate !== null) {
      setCheckMainButton(true);
    }; 
  }, [props.props.openDate]);

  return (
    <React.Fragment>
      <div className={checkMainButton ? 'shareButton_outContainer_active' : 'shareButton_outContainer'} onClick={() => { setCheckShare(!checkShare) }}>
        <img className='shareButton_share' alt='share' src='https://cdn-icons-png.flaticon.com/512/929/929539.png'></img>
      </div>
      <div className='shareButton_innerContainer'>
        <img className={checkShare ? 'shareButton_share_btn_url_active' : 'shareButton_share_btn'} alt='url' src='https://cdn-icons-png.flaticon.com/512/4050/4050374.png'></img>
        <img className={checkShare ? 'shareButton_share_btn_kakao_active' : 'shareButton_share_btn'} alt='kakao' src='https://cdn-icons-png.flaticon.com/512/3669/3669990.png'></img>
        <img className={checkShare ? 'shareButton_share_btn_facebook_active' : 'shareButton_share_btn'} alt='facebook' src='https://cdn-icons-png.flaticon.com/512/2168/2168281.png'></img>
        <img className={checkShare ? 'shareButton_share_btn_twitter_active' : 'shareButton_share_btn'} alt='twitter' src='https://cdn-icons-png.flaticon.com/512/2168/2168336.png'></img>
      </div>
    </React.Fragment>
  );
};

function Main() {

  const [checkStory, setCheckStory] = useState(true);
  const [userData, setUserData] = useState({ "nickname": 'null', "openDate": null });
  // const [letterData, setLetterData] = useState(null);

  console.log(userData.openDate);

  // Delete Cookie
  function delCookie() {
    const expires = new Date();
    expires.setDate(expires.getDate() - 1);
    const expiresDate = expires.toUTCString();
    let setCookie = '';
    setCookie += 'planetter=190301;';
    setCookie += 'Expires=' + expiresDate;
    document.cookie = setCookie;
  };

  useEffect(() => {
    const cookieData = document.cookie.split(';');
    for (let i = 0; i < cookieData.length; i++) {
      if (cookieData[i].indexOf('planetter') > -1) {
      } else {
        setCheckStory(false);
      } break;
    }; 
  }, []);

  return (
    <div className='main_background'>
      <ModalStory props={{ checkStory, setCheckStory }}></ModalStory>
      <ShareButton props={userData}></ShareButton>
      <Menu props={userData}></Menu>
      <NameOfPlanet props={{ userData, setUserData }}></NameOfPlanet>
      <h1 style={{ position: "absolute", right: "0", bottom: "20%" }}><NavLink end to="/login">login</NavLink></h1>
      <button style={{ position: "absolute", right: "0", bottom: "20%" }} onClick={delCookie}>Delete Cookie</button>
      <h1>{userData.openDate}</h1>
    </div>
  );
};

function App() {

  const navigater = useNavigate();
  const [userID, setUserID] = useState(1);

  useEffect(() => {
    // User device check.
    if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {
    } else {
      alert("모바일 환경에 최적화 되어 있습니다.");
    };
    if (userID === null) {
      navigater('/login');
    } else {
    };
  }, [navigater, userID])

  return (
    <React.Fragment>
      <Routes>
        <Route path="/" element={<Main />}></Route>
        <Route path="/*" element={<Main />}></Route>
        <Route path="/login" element={<Login props={userID} />}></Route>
        <Route path="/redirect" element={<Redirect props={setUserID} />}></Route>
        <Route path="/redirect2" element={<Redirect2 props={setUserID} />}></Route>
      </Routes>
    </React.Fragment>
  );
};

export default App;
