import React, { useEffect, useState } from 'react';
import { Routes, Route, NavLink, useNavigate } from "react-router-dom";
import Slider from "react-slick";
// import $ from "jquery";
import './App.css';


function Menu() {

  const [isWrap, setIsWrap] = useState(false);
  const [isMenu, setIsMenu] = useState(false);
  const [isInner, setIsInner] = useState(false);
  const [isMypage, setIsMypage] = useState(false);
  const [isPlanetter, setIsPlanetter] = useState(false);
  const [isHowto, setIsHowto] = useState(false);

  function toggleMenu() {
    if (isInner === false) {
      setIsMenu(!isMenu);
      setIsInner(false);
      setIsMypage(false);
      setIsPlanetter(false);
      setIsHowto(false);
      setIsWrap(false);
    } else if (isInner === true) {
      setIsInner(false);
      setIsMypage(false);
      setIsPlanetter(false);
      setIsHowto(false);
      setIsWrap(true);
    };
  };

  function toggleMypage() {
    setIsInner(!isInner);
    setIsMypage(!isMypage);
  };

  function togglePlanetter() {
    setIsInner(!isInner);
    setIsPlanetter(!isPlanetter);
  };

  function toggleHowto() {
    setIsInner(!isInner);
    setIsHowto(!isHowto);
  };

  return (
    <React.Fragment>
      <div className={isWrap ? "menu_wrap" : ""}>
        <div className="menu_outContainer">
          <div className={`menu_img${isMenu ? "_active" : ""}${isMypage || isPlanetter || isHowto ? "_plus" : ""}`} onClick={toggleMenu}></div>
        </div>
        <div className={`menu_bar${isMenu ? "_active" : ""}${isInner ? "_wide" : ""}`}>
          <div className={isInner ? "menu_bar_inner_true" : "menu_bar_inner"}>
            <p onClick={toggleMypage}>마이페이지</p>
            <p onClick={togglePlanetter}>Planetter</p>
            <p onClick={toggleHowto} >이용 방법</p>
            <p>Contact Us</p>
          </div>
          <div className={isMypage ? "menu_bar_mypage" : "menu_bar_mypage_true"}>
            <p>마이페이지</p>
          </div>
          <div className={isPlanetter ? "menu_bar_planetter" : "menu_bar_planetter_true"}>
            <p>Planetter</p>
          </div>
          <div className={isHowto ? "menu_bar_howto" : "menu_bar_howto_true"}>
            <p>이용방법</p>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

function DayCount() {

  const [Dday, setDday] = useState('아직 편지 받을 준비가 되지 않았어요.');

  function setDDay() {
    // Set the date we're counting down to
    let today = new Date();
    let thatDay = new Date();
    thatDay.setDate(today.getDate() + 10);
    let DDay = new Date(thatDay).getTime();
    // Update the count down every 1 second
    let count = setInterval(function () {
      // Get today's date and time
      let now = new Date().getTime();
      // Find the distance between now and the count down date
      let distance = DDay - now;
      // Time calculations for days, hours, minutes and seconds
      let days = Math.floor(distance / (1000 * 60 * 60 * 24));
      let hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
      let seconds = Math.floor((distance % (1000 * 60)) / 1000);
      // Output the result in an element with id="Dday"
      setDday(days + '일 ' + hours + '시간 ' + minutes + '분 ' + seconds + '초');
      // If the count down is over, write some text
      if (distance < 0) {
        clearInterval(count);
        setDday("편지를 열어보세요.");
      }
    }, 1000);
  };

  return (
    <React.Fragment>
      <button onClick={setDDay}>Start D-day</button>
      <h5>{Dday}</h5>
    </React.Fragment>
  )
}

function Main(props) {

  // Delete Cookie
  function delCookie() {
    const expires = new Date();
    expires.setDate(expires.getDate() - 1);
    const expiresDate = expires.toUTCString();
    let setCookie = '';
    setCookie += 'planetter=190301;';
    setCookie += 'Expires=' + expiresDate;
    document.cookie = setCookie;
  };

  useEffect(() => {
    const cookieData = document.cookie.split(';');
    for (let i = 0; i < cookieData.length; i++) {
      if (cookieData[i].indexOf('planetter') > -1) {
        console.log('Again!');
      } else {
        console.log('New Member!');
        props.props.setCheckStory(!props.props.checkStory);
      } break;
    }
  }, []);

  return (
    <div className='main_background'>
      <ModalStory props={props}></ModalStory>
      <Menu></Menu>
      <h1><NavLink end to="/login">Home</NavLink></h1>
      <DayCount></DayCount>
      <button onClick={delCookie}>Delete Cookie</button>
    </div>
  )
}

function ModalStory(props) {

  const [isChecked, setIsChecked] = useState(false);

  let settings = {
    draggable: false,
    swipe: false,
    arrows: true,
    dots: true,
    infinite: false,
    speed: 1250,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  function toggleCheck() {
    props.props.props.setCheckStory(!props.props.props.checkStory);
  };

  // Setting Cookie
  function setCookie() {
    setIsChecked(!isChecked);
    if (isChecked === false) {
      const expires = new Date();
      expires.setDate(expires.getDate() + 1);
      const expiresDate = expires.toUTCString();
      let setCookie = '';
      setCookie += 'planetter=190301;';
      setCookie += 'Expires=' + expiresDate;
      document.cookie = setCookie;
      console.log('SaveCookie!');
    } else {
      console.log('Opps!');
      delCookie();
    };
  };

  // Delete Cookie
  function delCookie() {
    const expires = new Date();
    expires.setDate(expires.getDate() - 1);
    const expiresDate = expires.toUTCString();
    let setCookie = '';
    setCookie += 'planetter=190301;';
    setCookie += 'Expires=' + expiresDate;
    document.cookie = setCookie;
  };

  return (
    <div className={props.props.props.checkStory ? "fadeStory" : "nonFadeStory"}>
      <div className="outContainer">
        <div className="modal">
          <div className="modal_close" onClick={toggleCheck}></div>
          <Slider {...settings}>
            <div className='outDiv'>
              <div>
                <div className='img1'></div>
                <h1 className="modal_title">PLANETTER</h1>
              </div>
            </div>
            <div className='outDiv'>
              <div>
                <div className='img2'></div>
                <h1 className="modal_title">PLANETTER</h1>
              </div>
            </div>
            <div className='outDiv'>
              <div>
                <div className='img3'></div>
                <h1 className="modal_title">PLANETTER</h1>
              </div>
            </div>
            <div className='outDiv'>
              <div>
                <div className='img4'></div>
                <h1 className="modal_title">PLANETTER</h1>
              </div>
            </div>
          </Slider>
          <div style={{ fontSize: "0.8rem", color: "aliceblue" }}><input type='checkbox' checked={isChecked} onChange={setCookie}></input> Meet again when there's a new story</div>
        </div>
      </div>
    </div>
  )
}

function Redirect(props) {

  const navigater = useNavigate();
  const urlParams = new URL(window.location.href).searchParams;
  const name = urlParams.get('code');
  const urlParamsErro = new URL(window.location.href).searchParams;
  const nameErro = urlParamsErro.get('error_description');

  useEffect(() => {
    if (nameErro === 'User denied access') {
      alert('로그인에 실패하였습니다.');
      navigater('/login');
    };
    const code = { code: name };
    const queryStringBody = Object.keys(code)
      .map(k => encodeURIComponent(k) + "=" + encodeURI(code[k]))
      .join("&");
    fetch("http://13.209.184.10:8080/api/kakao", {
      method: "POST",
      headers: {
        'content-type': 'application/x-www-form-urlencoded;charset=utf-8'
      },
      body: queryStringBody
    })
      .then(res => res.json())
      .then((data) => {
        props.props(data)
