import React, { useEffect, useState } from 'react';
import './NameOfPlanet.css';

function NameOfPlanet(props) {

    const [checkOpenDate, setCheckOpenDate] = useState(true);

    useEffect(() => {
        if (props.props.props.props.userData.openDate === null) {
            setCheckOpenDate(true);
        } else if (props.props.props.props.userData.openDate !== null) {
            setCheckOpenDate(false);
        };
    }, [props.props.props.props.userData.openDate]);

    function NoName() {
        return (
            <div className='noname_outContainer'>
                <h3>아직 신호를 보내지 않았습니다.</h3>
                <div className='noname_sendSignal'>신호 보내기</div>
                <div className='noname_sendSignal_outContainer'>
                    <div>
                    <h1>안녕, 잘 지내?</h1>
                    <h1>나는...</h1>
                    </div>
                    <h1>행성의 이름</h1>
                    <h1>시작 - 마감</h1>
                    <h1>Planetter는 10일의 시간을 제공합니다.</h1>
                    <div>
                        <p>Start</p>
                        <p>End</p>
                    </div>
                </div>
            </div>
        );
    };

    function YesName(props) {

        useEffect(() => {
            setDDay();
        }, [props.props.props.props.props.props.userData.openDate]);

        const [Dday, setDday] = useState(Number(props.props.props.props.props.props.userData.openDate));

        function setDDay() {
            let count = setInterval(function () {
                // Get today's date and time
                let now = new Date().getTime();
                // Find the distance between now and the count down date
                let distance = Dday - now;
                // Time calculations for days, hours, minutes and seconds
                let days = Math.floor(distance / (1000 * 60 * 60 * 24));
                let hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
                let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
                let seconds = Math.floor((distance % (1000 * 60)) / 1000);
                // Output the result in an element with id="Dday"
                setDday(days + '일 ' + hours + '시간 ' + minutes + '분 ' + seconds + '초');
                // If the count down is over, write some text
                if (distance < 0) {
                    clearInterval(count);
                    setDday("편지를 열어보세요.");
                };
            }, 0);
        };

        return (
            <div className='yesname_outContainer'>
                <h3>P-{props.props.props.props.props.props.userData.nickname}</h3>
                <h4>남은 시간</h4>
                <h4>{Dday}</h4>
            </div>
        );
    };

    return (
        <React.Fragment>
            {checkOpenDate ? <NoName props={{ props }}></NoName> : <YesName props={{ props }}></YesName>}

        </React.Fragment>
    );
};

export default NameOfPlanet;
