        // requireUserCheckData(result.userID);
        if (result.userID !== undefined) {
            // requireUserCheckData(result.userID);
            abcdfeg();
            let now = new Date().getTime();
            let distance = Number(userOpenDateRequired) - now;
            if (distance < 0) {
                if (userLetterCountRequired >= 36) {
                    alert('행성이 편지로 가득찼습니다. Pl@ter 페이지로 이동합니다.');
