    <React.Fragment>
      <Conditions></Conditions>
      <div className='login_wrap'>
        <div className='login_outContainer'>
          <div className='login_innerContainer'>
            <img alt='login_img' className='login_img' src='https://cdn-icons-png.flaticon.com/512/6598/6598519.png'></img>
            <h4 className='login_title'>로그인 해서 편지를 받아보세요</h4>
            <a href={`https://kauth.kakao.com/oauth/authorize?response_type=code&client_id=${process.env.REACT_APP_REST_API_KEY}&redirect_uri=${process.env.REACT_APP_REDIRECT}`}><img alt='login_kakao' className='login_kakao' src='http://papaspick.com/web/upload/2019_web/icon/kakao_login.jpg'></img></a>
