            </div>
            <p className='login_name'>©소소한프로젝트</p>
          </div>
        </div>
      </div>
    </React.Fragment>
  )
}

function App() {
  return (
    <React.Fragment>
      <Routes>
        <Route path="/" element={<Main />}></Route>
        <Route path="/*" element={<Main />}></Route>
        <Route path="/login" element={<Login />}></Route>
        <Route path="/redirect" element={<Redirect />}></Route>
      </Routes>
    </React.Fragment>
  );
}

export default App;
