import React, { useEffect, useState } from 'react';
import { Routes, Route, NavLink, useNavigate } from "react-router-dom";
// import Slider from "react-slick";
// import $ from "jquery";
import './App.css';
import Login from './components/Login';
import Redirect from './components/Redirect';
import ModalStory from './components/ModalStory';
import Menu from './components/Menu';

function DayCount() {

  const [Dday, setDday] = useState('아직 편지 받을 준비가 되지 않았어요.');

  function setDDay() {
    // Set the date we're counting down to
    let today = new Date();
    let thatDay = new Date();
    thatDay.setDate(today.getDate() + 10);
    let DDay = new Date(thatDay).getTime();
    // Update the count down every 1 second
    let count = setInterval(function () {
      // Get today's date and time
      let now = new Date().getTime();
      // Find the distance between now and the count down date
      let distance = DDay - now;
      // Time calculations for days, hours, minutes and seconds
      let days = Math.floor(distance / (1000 * 60 * 60 * 24));
      let hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
      let seconds = Math.floor((distance % (1000 * 60)) / 1000);
      // Output the result in an element with id="Dday"
      setDday(days + '일 ' + hours + '시간 ' + minutes + '분 ' + seconds + '초');
      // If the count down is over, write some text
      if (distance < 0) {
        clearInterval(count);
        setDday("편지를 열어보세요.");
      }
    }, 1000);
  };

  return (
    <React.Fragment>
      <button onClick={setDDay}>Start D-day</button>
      <h5>{Dday}</h5>
    </React.Fragment>
  )
}

function NameOfPlanet(props) {

  const [checkOpenDate, setCheckOpenDate] = useState(true);

  useEffect(() => {
    if (props.props.props.props.userData.openDate === null) {
      setCheckOpenDate(true);
    } else if (props.props.props.props.userData.openDate !== null) {
      setCheckOpenDate(false);
    };
  }, [props.props.props.props.userData.openDate])

  function NoName() {
    return (
      <div className='noname_outContainer'>
        <h3>아직 신호를 보내지 않았습니다.</h3>
      </div>
    )
  }

  function YesName(props) {
    return (
      <div>
        <h1>아직 신호를 보내지 않았습니다.</h1>
      </div>
    )
  }

  return (
    <div>
      {checkOpenDate ? <NoName></NoName> : <YesName props={{ props }}></YesName>}
      <button onClick={() => { setCheckOpenDate(!checkOpenDate) }}>button</button>
    </div>
  )
}

function Main(props) {

  // Delete Cookie
  function delCookie() {
    const expires = new Date();
    expires.setDate(expires.getDate() - 1);
    const expiresDate = expires.toUTCString();
    let setCookie = '';
    setCookie += 'planetter=190301;';
    setCookie += 'Expires=' + expiresDate;
    document.cookie = setCookie;
  };

  useEffect(() => {
    const cookieData = document.cookie.split(';');
    for (let i = 0; i < cookieData.length; i++) {
      if (cookieData[i].indexOf('planetter') > -1) {
        console.log('Again!');
      } else {
        console.log('New Member!');
        props.props.setCheckStory(!props.props.checkStory);
      } break;
    }
  }, []);

  return (
    <div className='main_background'>
      <ModalStory props={props}></ModalStory>
      <Menu></Menu>
      <NameOfPlanet props={{ props }}></NameOfPlanet>
      <h1><NavLink end to="/login">Home</NavLink></h1>
      <DayCount></DayCount>
      <button onClick={delCookie}>Delete Cookie</button>

    </div>
  )
}

function App() {

  const navigater = useNavigate();
  const [userID, setUserID] = useState(1);
  const [checkStory, setCheckStory] = useState(true);
  const [userData, setUserData] = useState({ "nickname": "사용자", "openDate": "1667215006334" });
  // const [letterData, setLetterData] = useState(null);

  useEffect(() => {
    if (userID === null) {
      navigater('/login');
    } else {
      console.log('Normal operation');
    }
  }, [navigater, userID])

  return (
    <React.Fragment>
      <Routes>
        <Route path="/" element={<Main props={{ checkStory, setCheckStory, userData, setUserData }} />}></Route>
        <Route path="/*" element={<Main props={{ checkStory, setCheckStory, userData, setUserData }} />}></Route>
        <Route path="/login" element={<Login props={userID} />}></Route>
        <Route path="/redirect" element={<Redirect props={setUserID} />}></Route>
      </Routes>
    </React.Fragment>
  );
}

export default App;
