import React, { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from "react-router-dom";
import Slider from "react-slick";
import $ from "jquery";
import './App.css';

function Main() {
  return (
    <React.Fragment>
      <h1>main</h1>
    </React.Fragment>
  )
}

function Ain() {


  return (
    <React.Fragment>
      <h1>mainddd</h1>
    </React.Fragment>
  )
}

function Login() {

  console.log(`${process.env.REACT_APP_REST_API_KEY}`);
  console.log(`${process.env.REACT_APP_REDIRECT}`);

  return (
    <div className='login_wrap'>
      <div className='login_outContainer'>
        <div className='login_innerContainer'>
          <img alt='login_img' className='login_img' src='https://cdn-icons-png.flaticon.com/512/6598/6598519.png'></img>
          <h4 className='login_title'>로그인 해서 편지를 받아보세요</h4>
          <a href={`https://kauth.kakao.com/oauth/authorize?response_type=code&client_id=${process.env.REACT_APP_REST_API_KEY}&redirect_uri=${process.env.REACT_APP_REDIRECT}`}><img alt='login_kakao' className='login_kakao' src='http://papaspick.com/web/upload/2019_web/icon/kakao_login.jpg'></img></a>
          <div className='login_text'>
            <span>이용약관</span><span>│</span><span>개인정보처리방침</span><span>│</span><span>ContactUs</span>
          </div>
          <p className='login_name'>©소소한프로젝트</p>
        </div>
      </div>
    </div>
  )
}

function App() {

  return (
    <React.Fragment>
      <Routes>
        <Route path="/" element={<Main />}></Route>
        <Route path="/*" element={<Main />}></Route>
        <Route path="/login" element={<Login />}></Route>
        <Route path="/redirect" element={<Ain />}></Route>
      </Routes>
    </React.Fragment>
  );
}

export default App;

