        <Route path="/*" element={<Main props={{ checkStory, setCheckStory }} />}></Route>
        <Route path="/login" element={<Login props={userID} />}></Route>
        <Route path="/redirect" element={<Redirect props={setUserID} />}></Route>
      </Routes>
    </React.Fragment>
  );
}

export default App;
